mapper.select:
select emp_no
from employees
where emp_no = #{empNo}

mapper.update:
update employees
set emp_name = #{empName}, emp_pino = #{empPino}, emp_id = #{empId}, emp_email = #{empEmail},
	emp_ph = #{empPh}, emp_addr = #{empAddr}, hire_date = #{hireDate}, porfilefile_name = #{profilefileName} emp_position = #{empPosition}
where emp_no = #{empNo}
